class Blugento_Campaign_Model_System_Config_Source_Status
{

    /**
     * Options getter
     *
     * @return array
     */
    public function toOptionArray()
    {
        return array(
            array('value' => 1, 'label'=>Mage::helper('blugento_campaign')->__('Enabled')),
            array('value' => 0, 'label'=>Mage::helper('blugento_campaign')->__('Disabled')),
        );
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        return array(
            0 => Mage::helper('blugento_campaign')->__('Disabled'),
            1 => Mage::helper('blugento_campaign')->__('Enabled'),
        );
    }

}
