class Blugento_ProductsWidget_Block_Adminhtml_Widget_Setup extends Mage_Adminhtml_Block_Widget
{
    /**
     * Prepare chooser element HTML
     *
     * @param Varien_Data_Form_Element_Abstract $element Form Element
     * @return Varien_Data_Form_Element_Abstract
     */
    public function prepareElementHtml(Varien_Data_Form_Element_Abstract $element)
    {
        $block = $this->getLayout()->createBlock('core/template')->setTemplate('blugento/productswidget/setup.phtml');
        $element->setData('after_element_html', $block->toHtml());
        return $element;
    }
}
